git filter-branch --commit-filter '
        if [ "$GIT_COMMITTER_NAME" != "1234" ];
        then
                GIT_COMMITTER_NAME="Austin Saunders";
                GIT_AUTHOR_NAME="Austin Saunders";
                GIT_COMMITTER_EMAIL="austin.saunders@coingi.com";
                GIT_AUTHOR_EMAIL="austin.saunders@coingi.com";
                git commit-tree "$@";
        else
                git commit-tree "$@";
        fi' HEAD
